mapper.update:
UPDATE QNA
SET BOARD_REF = #{ boardNum }
WHERE BOARD_NUM = #{ boardNum }

UPDATE QNA
SET BOARD_STEP = BOARD_STEP + 1
WHERE BOARD_REF = #{ boardRef } AND BOARD_STEP > #{ boardStep }

UPDATE QNA
SET BOARD_TITLE = #{ boardTitle }
	, BOARD_CONTENT = #{ boardContent }
	, BOARD_DATE = NOW()
WHERE BOARD_NUM = #{ boardNum }

mapper.select:
SELECT *
FROM QNA_FILE
WHERE FILE_NUM = #{ fileNum }

mapper.update:
UPDATE QNA
SET BOARD_DATE = NULL
WHERE BOARD_NUM = #{ boardNum }
